(128, 1, '2018-09-06 13:05:19', '2018-09-06 13:05:19', '<div class=\"tab\">\r\n  <button class=\"tablinks\" onclick=\"openCity(event, \'London\')\" id=\"defaultOpen\">London</button>\r\n  <button class=\"tablinks\" onclick=\"openCity(event, \'Paris\')\">Paris</button>\r\n  <button class=\"tablinks\" onclick=\"openCity(event, \'Tokyo\')\">Tokyo</button>\r\n</div>\r\n\r\n<div id=\"London\" class=\"tabcontent\">\r\n  <h3>London</h3>\r\n  <p>London is the capital city of England.</p>\r\n</div>\r\n\r\n<div id=\"Paris\" class=\"tabcontent\">\r\n  <h3>Paris</h3>\r\n  <p>Paris is the capital of France.</p> \r\n</div>\r\n\r\n<div id=\"Tokyo\" class=\"tabcontent\">\r\n  <h3>Tokyo</h3>\r\n  <p>Tokyo is the capital of Japan.</p>\r\n</div>\r\n\r\n<script>\r\nfunction openCity(evt, cityName) {\r\n    var i, tabcontent, tablinks;\r\n    tabcontent = document.getElementsByClassName(\"tabcontent\");\r\n    for (i = 0; i < tabcontent.length; i++) {\r\n        tabcontent[i].style.display = \"none\";\r\n    }\r\n    tablinks = document.getElementsByClassName(\"tablinks\");\r\n    for (i = 0; i < tablinks.length; i++) {\r\n        tablinks[i].className = tablinks[i].className.replace(\" active\", \"\");\r\n    }\r\n    document.getElementById(cityName).style.display = \"block\";\r\n    evt.currentTarget.className += \" active\";\r\n}\r\n\r\n// Get the element with id=\"defaultOpen\" and click on it\r\ndocument.getElementById(\"defaultOpen\").click();\r\n</script>', 'Services', '', 'inherit', 'closed', 'closed', '', '12-revision-v1', '', '', '2018-09-06 13:05:19', '2018-09-06 13:05:19', '', 12, 'http://localhost/dental1/2018/09/06/12-revision-v1/', 0, 'revision', '', 0),
